import React from 'react'

export const Card = (A) => {
    const { name, email, id } = A;
    return (
        <div className='bg-light-yellow dib br3 pa3 ma2 grow bw2 shadow-5'>
            <img src= {A.img} alt='robot' />      
            <h1>{A.name}</h1>      
            <>{A.text}</>
        </div>
    )
}
export default Card
